    import React, { createContext, useContext, useState, useCallback } from 'react';

    const AppContext = createContext();

    export const AppProvider = ({ children }) => {
    const [wishes, setWishes] = useState([]);
    const [reminders, setReminders] = useState([]);

    const loadWishes = useCallback(() => {
        const storedWishes = JSON.parse(localStorage.getItem('wishes') || '[]');
        setWishes(storedWishes);
    }, []);

    const addWish = useCallback((newWish) => {
        const updatedWishes = [...wishes, { ...newWish, id: Date.now() }];
        setWishes(updatedWishes);
        localStorage.setItem('wishes', JSON.stringify(updatedWishes));
    }, [wishes]);

    const addReminder = useCallback((newReminder) => {
        const updatedReminders = [...reminders, { ...newReminder, id: Date.now(), fulfilled: false }];
        setReminders(updatedReminders);
        localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    }, [reminders]);

    const fulfillReminder = useCallback((id) => {
        const updatedReminders = reminders.map(reminder => 
        reminder.id === id ? { ...reminder, fulfilled: true } : reminder
        );
        setReminders(updatedReminders);
        localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    }, [reminders]);

    const deleteReminder = useCallback((id) => {
        const updatedReminders = reminders.filter(reminder => reminder.id !== id);
        setReminders(updatedReminders);
        localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    }, [reminders]);

    return (
        <AppContext.Provider value={{ wishes, loadWishes, addWish, reminders, addReminder, fulfillReminder, deleteReminder }}>
        {children}
        </AppContext.Provider>
    );
    };

    export const useApp = () => useContext(AppContext);
