    import React, { useState, useEffect } from 'react';
    import { database } from '../services/firebase';
    import { ref, push, remove, onValue } from 'firebase/database';

    const WishList = () => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ title: '', date: '', location: '' });

    useEffect(() => {
        const eventsRef = ref(database, 'events');
        const unsubscribe = onValue(eventsRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
            const eventList = Object.entries(data).map(([key, value]) => ({
            id: key,
            ...value,
            }));
            setEvents(eventList);
        } else {
            setEvents([]);
        }
        });

        return () => unsubscribe();
    }, []);

    const addEvent = (e) => {
        e.preventDefault();
        const eventsRef = ref(database, 'events');
        push(eventsRef, newEvent);
        setNewEvent({ title: '', date: '', location: '' });
    };

    const deleteEvent = (id) => {
        const eventRef = ref(database, `events/${id}`);
        remove(eventRef);
    };

    return (
        <div className="page">
        <h2 className="page-title">Wish List</h2>
        <form onSubmit={addEvent} className="event-form">
            <input
            type="text"
            placeholder="Event title"
            value={newEvent.title}
            onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
            className="input"
            required
            />
            <input
            type="date"
            value={newEvent.date}
            onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
            className="input"
            required
            />
            <input
            type="text"
            placeholder="Location"
            value={newEvent.location}
            onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
            className="input"
            required
            />
            <button type="submit" className="button">Add Event</button>
        </form>
        <ul className="event-list">
            {events.map((event) => (
            <li key={event.id} className="event-item">
                <strong>{event.title}</strong> - {event.date} at {event.location}
                <button onClick={() => deleteEvent(event.id)} className="delete-button">Delete</button>
            </li>
            ))}
        </ul>
        </div>
    );
    };

    export default WishList;
