import React, { useState, useEffect } from 'react';
import { auth, database } from '../services/firebase';
import { ref, onValue, update } from 'firebase/database';

const Invitations = () => {
    const [invitations, setInvitations] = useState([]);

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const invitationsRef = ref(database, 'wishLists');
            const unsubscribe = onValue(invitationsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const userInvitations = Object.entries(data)
                        .filter(([_, value]) => 
                            value.collaborators && 
                            Object.values(value.collaborators).some(collab => 
                                collab.email === currentUser.email && collab.status === 'pending'
                            )
                        )
                        .map(([key, value]) => ({ id: key, ...value }));
                    setInvitations(userInvitations);
                } else {
                    setInvitations([]);
                }
            });
            return () => unsubscribe();
        }
    }, []);

    const acceptInvitation = async (listId) => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const listRef = ref(database, `wishLists/${listId}/collaborators`);
            await update(listRef, {
                [currentUser.uid]: {
                    email: currentUser.email,
                    status: 'accepted'
                }
            });
            alert('Invitación aceptada');
        }
    };

    return (
        <div className="invitations-section">
            <h3 className="section-title">Invitaciones pendientes</h3>
            {invitations.length === 0 ? (
                <p className="no-invitations">No tienes invitaciones pendientes</p>
            ) : (
                <ul className="invitation-list">
                    {invitations.map((invitation) => (
                        <li key={invitation.id} className="invitation-item">
                            <span className="invitation-from">Invitación para colaborar en una lista de deseos</span>
                            <button onClick={() => acceptInvitation(invitation.id)} className="button accept-button">
                                Aceptar
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Invitations;
