import React, { useState, useEffect } from 'react';
import { database, auth } from '../services/firebase';
import { ref, push, set, onValue, update } from 'firebase/database';

const Collaborate = () => {
    const [email, setEmail] = useState('');
    const [invitations, setInvitations] = useState([]);
    const [wishListId, setWishListId] = useState('');

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const userRef = ref(database, `users/${currentUser.uid}`);
            onValue(userRef, (snapshot) => {
                const userData = snapshot.val();
                if (userData && userData.wishListId) {
                    setWishListId(userData.wishListId);
                }
            });

            const invitationsRef = ref(database, `wishLists/${wishListId}/collaborators`);
            onValue(invitationsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const invitationList = Object.entries(data).map(([key, value]) => ({ id: key, ...value }));
                    setInvitations(invitationList);
                } else {
                    setInvitations([]);
                }
            });
        }
    }, [wishListId]);

    const inviteCollaborator = async () => {
        const currentUser = auth.currentUser;
        if (currentUser && wishListId) {
            try {
                const collaboratorsRef = ref(database, `wishLists/${wishListId}/collaborators`);
                const newCollaboratorRef = push(collaboratorsRef);
                await set(newCollaboratorRef, {
                    email: email,
                    status: 'pending'
                });
                alert('Invitación enviada a ' + email);
                setEmail('');
            } catch (error) {
                console.error('Error al enviar invitación:', error);
                alert('Error al enviar la invitación. Por favor, intenta de nuevo.');
            }
        } else {
            alert('Debes estar autenticado y tener una lista de deseos para invitar colaboradores.');
        }
    };

    return (
        <div className="page">
            <h2 className="page-title">Invitar Colaborador</h2>
            <div className="invite-form">
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email del colaborador"
                    className="input"
                />
                <button onClick={inviteCollaborator} className="button">
                    Enviar Invitación
                </button>
            </div>
            <div className="invitations-sent-section">
                <h3 className="section-title">Invitaciones enviadas:</h3>
                {invitations.length === 0 ? (
                    <p className="no-invitations">No has enviado invitaciones aún.</p>
                ) : (
                    <ul className="invitation-list">
                        {invitations.map((invitation) => (
                            <li key={invitation.id} className="invitation-item">
                                <span className="invitation-to">Invitación enviada a: {invitation.email}</span>
                                <span className="invitation-status">Estado: {invitation.status}</span>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default Collaborate;
