import React, { useState, useEffect } from 'react';
import { database } from '../services/firebase';
import { ref, push, remove, onValue } from 'firebase/database';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import { OpenStreetMapProvider } from 'leaflet-geosearch';
import 'leaflet/dist/leaflet.css';
import 'leaflet-geosearch/dist/geosearch.css';
import '../App.css';

const categories = ["Viajes", "Restaurantes", "Experiencias", "Películas y Series", "Eventos", "Regalos Mutuos", "Hobbies y Proyectos", "Deportes y Aventura", "Mejoras del Hogar", "Lectura"];
const provider = new OpenStreetMapProvider();

const WishList = () => {
const [events, setEvents] = useState([]);
const [newEvent, setNewEvent] = useState({ 
    title: '', 
    date: new Date().toISOString().split('T')[0],
    location: '', 
    category: 'Other',
    coordinates: [51.505, -0.09]
});
const [showMap, setShowMap] = useState(false);
const [searchResults, setSearchResults] = useState([]);
const [error, setError] = useState(null);

useEffect(() => {
    const eventsRef = ref(database, 'events');
    const unsubscribe = onValue(eventsRef, (snapshot) => {
    try {
        const data = snapshot.val();
        if (data) {
            const eventList = Object.entries(data).map(([key, value]) => ({
            id: key,
            ...value,
            }));
            setEvents(eventList);
        } else {
            setEvents([]);
        }
    } catch (error) {
        console.error('Error al leer eventos:', error);
        setError('Error al cargar eventos. Por favor, intenta de nuevo más tarde.');
    }
    }, (error) => {
        console.error('Error en la suscripción a eventos:', error);
        setError('Error al suscribirse a eventos. Por favor, verifica tu conexión e intenta de nuevo.');
    });

    return () => unsubscribe();
}, []);

const addEvent = (e) => {
    e.preventDefault();
    const eventsRef = ref(database, 'events');
    push(eventsRef, {
    ...newEvent,
    coordinates: newEvent.coordinates.map(coord => parseFloat(coord.toFixed(6)))
    }).then(() => {
    console.log('Evento añadido con éxito');
    setNewEvent({ 
        title: '', 
        date: new Date().toISOString().split('T')[0],
        location: '', 
        category: 'Other',
        coordinates: [51.505, -0.09]
    });
    setShowMap(false);
    setSearchResults([]);
    }).catch((error) => {
    console.error('Error al añadir evento:', error);
    setError('Error al añadir evento. Por favor, intenta de nuevo.');
    });
};

const deleteEvent = (id) => {
    const eventRef = ref(database, `events/${id}`);
    remove(eventRef).then(() => {
    console.log('Evento eliminado con éxito');
    }).catch((error) => {
    console.error('Error al eliminar evento:', error);
    setError('Error al eliminar evento. Por favor, intenta de nuevo.');
    });
};

const handleLocationSearch = async (query) => {
    const results = await provider.search({ query });
    setSearchResults(results);
};

const selectLocation = (result) => {
    setNewEvent(prev => ({
    ...prev,
    location: result.label,
    coordinates: [result.y, result.x]
    }));
    setSearchResults([]);
    setShowMap(true);
};

const LocationPicker = () => {
    useMapEvents({
    click(e) {
        setNewEvent(prev => ({ ...prev, coordinates: [e.latlng.lat, e.latlng.lng] }));
    },
    });

    return (
    <Marker position={newEvent.coordinates}>
        <Popup>{newEvent.location || 'Selected location'}</Popup>
    </Marker>
    );
};

return (
    <div className="page">
    <h2 className="page-title">Lista de Deseos</h2>
    {error && <div className="error">{error}</div>}
    <form onSubmit={addEvent} className="event-form">
        <input
        type="text"
        placeholder="Título del evento"
        value={newEvent.title}
        onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
        className="input"
        required
        />
        <input
        type="date"
        value={newEvent.date}
        onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
        className="input"
        required
        />
        <input
        type="text"
        placeholder="Ubicación"
        value={newEvent.location}
        onChange={(e) => {
            setNewEvent({ ...newEvent, location: e.target.value });
            handleLocationSearch(e.target.value);
        }}
        className="input"
        />
        {searchResults.length > 0 && (
        <ul className="search-results">
            {searchResults.map((result, index) => (
            <li key={index} onClick={() => selectLocation(result)}>
                {result.label}
            </li>
            ))}
        </ul>
        )}
        <select
        value={newEvent.category}
        onChange={(e) => setNewEvent({ ...newEvent, category: e.target.value })}
        className="input"
        >
        {categories.map(category => (
            <option key={category} value={category}>{category}</option>
        ))}
        </select>
        <button type="submit" className="button">Añadir Evento</button>
    </form>
    {showMap && (
        <div style={{ height: '300px', marginBottom: '20px' }}>
        <MapContainer center={newEvent.coordinates} zoom={13} style={{ height: '100%', width: '100%' }}>
            <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <LocationPicker />
        </MapContainer>
        </div>
    )}
    <ul className="event-list">
    {events.map((event) => (
        <li key={event.id} className="event-item">
            <div className="event-item-content">
            <div className="event-item-title">{event.title}</div>
            <div className="event-item-details">
                {new Date(event.date).toLocaleDateString()} en {event.location}
                <br />
                Categoría: {event.category}
            </div>
            </div>
            <button onClick={() => deleteEvent(event.id)} className="delete-button">Eliminar</button>
        </li>
        ))}
    </ul>
    </div>
);
};

export default WishList;
