    import React, { useState } from 'react';
    import '../styles/WishForm.css';

    const WishForm = ({ addWish }) => {
    const [wish, setWish] = useState({
        title: '',
        description: '',
        category: '',
        location: '',
        date: ''
    });
    const [locationSuggestions, setLocationSuggestions] = useState([]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setWish({ ...wish, [name]: value });

        if (name === 'location') {
        searchLocation(value);
        }
    };

    const searchLocation = async (query) => {
        if (query.length > 2) {
        const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${query}`);
        const data = await response.json();
        setLocationSuggestions(data.slice(0, 5));
        } else {
        setLocationSuggestions([]);
        }
    };

    const selectLocation = (location) => {
        setWish({ ...wish, location: location.display_name });
        setLocationSuggestions([]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addWish(wish);
        setWish({
        title: '',
        description: '',
        category: '',
        location: '',
        date: ''
        });
    };

    return (
        <form className="wish-form" onSubmit={handleSubmit}>
        <h2>Nuevo Deseo</h2>
        <div className="input-group">
            <input
            type="text"
            name="title"
            value={wish.title}
            onChange={handleChange}
            placeholder="¿Cuál es tu deseo?"
            required
            />
        </div>
        <div className="input-group">
            <textarea
            name="description"
            value={wish.description}
            onChange={handleChange}
            placeholder="Describe tu deseo..."
            required
            />
        </div>
        <div className="input-group">
            <select
            name="category"
            value={wish.category}
            onChange={handleChange}
            required
            >
            <option value="">Categoría</option>
            <option value="viajes">Viajes</option>
            <option value="comidas">Comidas</option>
            <option value="actividades">Actividades</option>
            <option value="otros">Otros</option>
            </select>
        </div>
        <div className="input-group">
            <input
            type="text"
            name="location"
            value={wish.location}
            onChange={handleChange}
            placeholder="¿Dónde?"
            required
            />
            {locationSuggestions.length > 0 && (
            <ul className="location-suggestions">
                {locationSuggestions.map((location) => (
                <li key={location.place_id} onClick={() => selectLocation(location)}>
                    {location.display_name}
                </li>
                ))}
            </ul>
            )}
        </div>
        <div className="input-group">
            <input
            type="date"
            name="date"
            value={wish.date}
            onChange={handleChange}
            required
            />
        </div>
        <button type="submit">Agregar Deseo</button>
        </form>
    );
    };

    export default WishForm;
