    import React, { useState, useEffect } from 'react';
    import { useApp } from '../context/AppContext';

    const RemindersPage = () => {
    const { reminders, addReminder, fulfillReminder, deleteReminder } = useApp();
    const [newReminder, setNewReminder] = useState({ title: '', date: '' });

    useEffect(() => {
        const storedReminders = JSON.parse(localStorage.getItem('reminders') || '[]');
        if (storedReminders.length > 0 && reminders.length === 0) {
            // Aquí deberías tener una función para establecer los recordatorios en el contexto
            // Por ejemplo: setReminders(storedReminders);
        }
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewReminder(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (newReminder.title && newReminder.date) {
        addReminder(newReminder);
        setNewReminder({ title: '', date: '' });
        }
    };

    const upcomingReminders = reminders.filter(reminder => !reminder.fulfilled);
    const fulfilledReminders = reminders.filter(reminder => reminder.fulfilled);

    return (
        <div className="relative flex size-full min-h-screen flex-col bg-[#FFFFFF] group/design-root overflow-x-hidden" style={{ fontFamily: 'Manrope, "Noto Sans", sans-serif' }}>
        <div className="layout-container flex h-full grow flex-col">
            <div className="gap-1 px-6 flex flex-1 justify-center py-5">
            <div className="layout-content-container flex flex-col w-80">
                <h2 className="text-black text-lg font-bold leading-tight tracking-[-0.015em] px-4 text-left pb-2 pt-4">Upcoming</h2>
                <ul className="space-y-2">
                {upcomingReminders.map(reminder => (
                    <li key={reminder.id} className="flex justify-between items-center bg-gray-100 p-2 rounded">
                    <span>{reminder.title} - {reminder.date}</span>
                    <button onClick={() => fulfillReminder(reminder.id)} className="text-green-500">Fulfill</button>
                    </li>
                ))}
                </ul>
                <h2 className="text-black text-lg font-bold leading-tight tracking-[-0.015em] px-4 text-left pb-2 pt-4">Fulfilled</h2>
                <ul className="space-y-2">
                {fulfilledReminders.map(reminder => (
                    <li key={reminder.id} className="flex justify-between items-center bg-gray-200 p-2 rounded">
                    <span>{reminder.title} - {reminder.date}</span>
                    <button onClick={() => deleteReminder(reminder.id)} className="text-red-500">Delete</button>
                    </li>
                ))}
                </ul>
            </div>
            <div className="layout-content-container flex flex-col max-w-[960px] flex-1">
                <div className="flex flex-wrap justify-between gap-3 p-4">
                <div className="flex min-w-72 flex-col gap-3">
                    <p className="text-black tracking-light text-[32px] font-bold leading-tight">Reminders</p>
                    <p className="text-[#6B6B6B] text-sm font-normal leading-normal">Add a reminder to get notified when it's time to give a wish.</p>
                </div>
                </div>
                <form onSubmit={handleSubmit} className="space-y-4 p-4">
                <div>
                    <label htmlFor="title" className="block text-sm font-medium text-gray-700">Reminder Title</label>
                    <input
                    type="text"
                    id="title"
                    name="title"
                    value={newReminder.title}
                    onChange={handleInputChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                    required
                    />
                </div>
                <div>
                    <label htmlFor="date" className="block text-sm font-medium text-gray-700">Reminder Date</label>
                    <input
                    type="date"
                    id="date"
                    name="date"
                    value={newReminder.date}
                    onChange={handleInputChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                    required
                    />
                </div>
                <button type="submit" className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
                    Add Reminder
                </button>
                </form>
            </div>
            </div>
        </div>
        </div>
    );
    };

    export default RemindersPage;
