import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useApp } from '../context/AppContext';

const ActivityPage = () => {
    const { wishes, loadWishes } = useApp();

    useEffect(() => {
        loadWishes();
    }, [loadWishes]);

    const recentWishes = wishes.slice(-5).reverse();

    return (
        <div className="container mx-auto px-4 py-8">
            <h1 className="text-3xl font-bold mb-6">Recent Activity</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                    <h2 className="text-2xl font-semibold mb-4">Recent Wishes</h2>
                    {recentWishes.map((wish) => (
                        <div key={wish.id} className="bg-white p-4 rounded-lg shadow mb-4">
                            <h3 className="font-semibold text-lg">{wish.title}</h3>
                            <p className="text-gray-600">{wish.description}</p>
                            {wish.targetDate && (
                                <p className="text-sm text-gray-500">Target Date: {wish.targetDate}</p>
                            )}
                        </div>
                    ))}
                </div>
                <div>
                    <h2 className="text-2xl font-semibold mb-4">Add a Wish</h2>
                    <Link
                        to="/new-wish"
                        className="block w-full bg-blue-500 text-white text-center py-2 rounded hover:bg-blue-600"
                    >
                        Create New Wish
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default ActivityPage;
