    import React from 'react';
    import { Link, useLocation } from 'react-router-dom';
    import { MapPin, Calendar, List, User, LogOut, Users } from 'lucide-react';
    import { auth } from '../services/firebase';
    import { signOut } from 'firebase/auth';

    const Navigation = () => {
    const location = useLocation();

    const handleLogout = async () => {
        try {
        await signOut(auth);
        } catch (error) {
        console.error('Logout error:', error);
        }
    };

    return (
        <nav className="nav">
        <ul>
            <li>
            <Link to="/" className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}>
                <List size={24} />
                <span>Lista de Deseos</span>
            </Link>
            </li>
            <li>
            <Link to="/map" className={`nav-link ${location.pathname === '/map' ? 'active' : ''}`}>
                <MapPin size={24} />
                <span>Mapa</span>
            </Link>
            </li>
            <li>
            <Link to="/reminders" className={`nav-link ${location.pathname === '/reminders' ? 'active' : ''}`}>
                <Calendar size={24} />
                <span>Recordatorios</span>
            </Link>
            </li>
            <li>
            <Link to="/collaborate" className={`nav-link ${location.pathname === '/collaborate' ? 'active' : ''}`}>
                <Users size={24} />
                <span>Colaborar</span>
            </Link>
            </li>
            <li>
            <Link to="/profile" className={`nav-link ${location.pathname === '/profile' ? 'active' : ''}`}>
                <User size={24} />
                <span>Perfil</span>
            </Link>
            </li>
            <li>
            <button onClick={handleLogout} className="nav-link logout-button">
                <LogOut size={24} />
                <span>Cerrar sesi√≥n</span>
            </button>
            </li>
        </ul>
        </nav>
    );
    };

    export default Navigation;
