    import React, { useState, useEffect } from 'react';
    import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
    import { database } from '../services/firebase';
    import { ref, onValue } from 'firebase/database';
    import 'leaflet/dist/leaflet.css';

    const Map = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const eventsRef = ref(database, 'events');
        const unsubscribe = onValue(eventsRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
            const eventList = Object.values(data);
            setEvents(eventList);
        } else {
            setEvents([]);
        }
        });

        return () => unsubscribe();
    }, []);

    const nextEvent = events.find(event => new Date(event.date) >= new Date());

    if (!nextEvent) {
        return <div className="page"><h2 className="page-title">No upcoming events</h2></div>;
    }

    // You would need to implement a geocoding service to convert location to coordinates
    // For this example, we'll use a placeholder location
    const position = [51.505, -0.09];

    return (
        <div className="page">
        <h2 className="page-title">Map</h2>
        <div style={{ height: '400px', width: '100%' }}>
            <MapContainer center={position} zoom={13} style={{ height: '100%', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <Marker position={position}>
                <Popup>
                {nextEvent.title} <br /> {nextEvent.date}
                </Popup>
            </Marker>
            </MapContainer>
        </div>
        </div>
    );
    };

    export default Map;
