    import React, { useState, useEffect } from 'react';
    import { database, auth } from '../services/firebase';
    import { ref, onValue } from 'firebase/database';

    const Reminders = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const sharedListsRef = ref(database, 'sharedLists');
            const unsubscribe = onValue(sharedListsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const sharedList = Object.entries(data).find(([_, value]) => 
                        value.accepted && value.owners.includes(currentUser.uid)
                    );
                    if (sharedList && sharedList[1].events) {
                        const sharedEvents = Object.entries(sharedList[1].events).flatMap(([uid, events]) => 
                            Object.values(events || {}).map(event => ({
                                ...event,
                                owner: uid
                            }))
                        );
                        setEvents(sharedEvents);
                    } else {
                        loadPersonalEvents(currentUser.uid);
                    }
                } else {
                    loadPersonalEvents(currentUser.uid);
                }
            });
    
            return () => unsubscribe();
        }
    }, []);
    
    const loadPersonalEvents = (userId) => {
        const eventsRef = ref(database, `users/${userId}/events`);
        onValue(eventsRef, (snapshot) => {
            const data = snapshot.val();
            if (data) {
                const eventList = Object.values(data);
                setEvents(eventList);
            } else {
                setEvents([]);
            }
        });
    };
    
    const sortedEvents = events.sort((a, b) => new Date(a.date) - new Date(b.date));
    const nextEvent = sortedEvents.find(event => new Date(event.date) >= new Date());
    const pastEvents = sortedEvents.filter(event => new Date(event.date) < new Date());
    
    return (
        <div className="page">
            <h2 className="page-title">Recordatorios</h2>
            {nextEvent ? (
                <div className="next-event">
                    <h3 className="next-event-title">Próximo Evento</h3>
                    <div className="next-event-content">
                        <div className="next-event-title">{nextEvent.title}</div>
                        <div className="next-event-details">
                            <span className="next-event-date">{new Date(nextEvent.date).toLocaleDateString()}</span>
                            <span className="next-event-location">{nextEvent.location}</span>
                            <span className="next-event-category">{nextEvent.category}</span>
                        </div>
                    </div>
                </div>
            ) : (
                <p className="no-events-message">No tienes recordatorios pendientes</p>
            )}
            {pastEvents.length > 0 ? (
                <>
                    <h3 className="section-title">Eventos Pasados</h3>
                    <ul className="event-list">
                        {pastEvents.map((event, index) => (
                            <li key={index} className="event-item">
                                <div className="event-item-content">
                                    <div className="event-item-title">{event.title}</div>
                                    <div className="event-item-details">
                                        <span className="event-item-date">{new Date(event.date).toLocaleDateString()}</span>
                                        <span className="event-item-location">{event.location}</span>
                                        <span className="event-item-category">{event.category}</span>
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                </>
            ) : (
                <p className="no-events-message">No tienes eventos pasados</p>
            )}
        </div>
    );
    };

    export default Reminders;
