    import React, { createContext, useContext, useState, useEffect } from 'react';

    const AppContext = createContext();

    export const AppProvider = ({ children }) => {
    const [wishes, setWishes] = useState([]);
    const [reminders, setReminders] = useState([]);

    useEffect(() => {
        loadWishes();
        loadReminders();
    }, []);

    const loadWishes = () => {
        const savedWishes = JSON.parse(localStorage.getItem('wishes')) || [];
        setWishes(savedWishes);
    };

    const loadReminders = () => {
        const savedReminders = JSON.parse(localStorage.getItem('reminders')) || [];
        setReminders(savedReminders);
    };

    const addWish = (wish) => {
        const newWishes = [...wishes, { ...wish, id: Date.now() }];
        setWishes(newWishes);
        localStorage.setItem('wishes', JSON.stringify(newWishes));
    };

    const deleteWish = (id) => {
        const updatedWishes = wishes.filter(wish => wish.id !== id);
        setWishes(updatedWishes);
        localStorage.setItem('wishes', JSON.stringify(updatedWishes));
    };

    const addReminder = (reminder) => {
        const newReminders = [...reminders, { ...reminder, id: Date.now(), fulfilled: false }];
        setReminders(newReminders);
        localStorage.setItem('reminders', JSON.stringify(newReminders));
    };

    const fulfillReminder = (id) => {
        const updatedReminders = reminders.map(reminder =>
        reminder.id === id ? { ...reminder, fulfilled: true } : reminder
        );
        setReminders(updatedReminders);
        localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    };

    const deleteReminder = (id) => {
        const updatedReminders = reminders.filter(reminder => reminder.id !== id);
        setReminders(updatedReminders);
        localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    };

    return (
        <AppContext.Provider value={{
        wishes,
        reminders,
        loadWishes,
        loadReminders,
        addWish,
        deleteWish,
        addReminder,
        fulfillReminder,
        deleteReminder
        }}>
        {children}
        </AppContext.Provider>
    );
    };

    export const useApp = () => useContext(AppContext);
