import React from 'react';
import { useAppContext } from '../context/AppContext';
import '../styles/DashboardPage.css';

const DashboardPage = () => {
  const { wishes, rewards, achievements } = useAppContext();

  const completedWishes = wishes.filter(wish => wish.completed).length;

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      <p>Keep up the good work, Jane</p>

      <div className="dashboard-stats">
        <div className="stat-box">
          <h2>{wishes.length}</h2>
          <p>Total Wishes</p>
        </div>
        <div className="stat-box">
          <h2>{completedWishes}</h2>
          <p>Completed Wishes</p>
        </div>
        <div className="stat-box">
          <h2>{rewards.length}</h2>
          <p>Total Rewards</p>
        </div>
        <div className="stat-box">
          <h2>{achievements.length}</h2>
          <p>Unlocked Achievements</p>
        </div>
      </div>

      {/* Resto del componente... */}
    </div>
  );
};

export default DashboardPage;
