    import React, { useState, useEffect } from 'react';
    import { database, auth } from '../services/firebase';
    import { ref, push, set, onValue } from 'firebase/database';

    const Collaborate = () => {
        const [email, setEmail] = useState('');
        const [invitations, setInvitations] = useState([]);
    
        useEffect(() => {
            const currentUser = auth.currentUser;
            if (currentUser) {
                const invitationsRef = ref(database, 'sharedLists');
                onValue(invitationsRef, (snapshot) => {
                    const data = snapshot.val();
                    if (data) {
                        const invitationList = Object.entries(data)
                            .filter(([_, value]) => value.owners.includes(currentUser.uid))
                            .map(([key, value]) => ({ id: key, ...value }));
                        setInvitations(invitationList);
                    }
                });
            }
        }, []);
    
        const inviteCollaborator = async () => {
            const currentUser = auth.currentUser;
            if (currentUser) {
                try {
                    const newListRef = push(ref(database, 'sharedLists'));
                    await set(newListRef, {
                        owners: [currentUser.uid],
                        invitedEmail: email
                    });
                    alert('Invitación guardada. Se enviará un correo electrónico a ' + email + ' pronto.');
                    setEmail('');
                } catch (error) {
                    console.error('Error al enviar invitación:', error);
                    alert('Error al guardar la invitación. Por favor, intenta de nuevo.');
                }
            } else {
                alert('Debes estar autenticado para enviar invitaciones.');
            }
        };
    
        return (
            <div className="page">
                <h2 className="page-title">Invitar Colaborador</h2>
                <div className="invite-form">
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email del colaborador"
                        className="input"
                    />
                    <button onClick={inviteCollaborator} className="button">
                        Enviar Invitación
                    </button>
                </div>
                <div className="invitations-sent-section">
                    <h3 className="section-title">Invitaciones enviadas:</h3>
                    {invitations.length === 0 ? (
                        <p className="no-invitations">No has enviado invitaciones aún.</p>
                    ) : (
                        <ul className="invitation-list">
                            {invitations.map((invitation) => (
                                <li key={invitation.id} className="invitation-item">
                                    <span className="invitation-to">Invitación enviada a: {invitation.invitedEmail}</span>
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </div>
        );
    }

    export default Collaborate;
