import React, { useState, useEffect } from 'react';
import { database, auth } from '../services/firebase';
import { ref, push, set, onValue, get } from 'firebase/database';

const Collaborate = () => {
    const [email, setEmail] = useState('');
    const [invitations, setInvitations] = useState([]);
    const [hasWishList, setHasWishList] = useState(false);

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            // Verificar si el usuario tiene una lista de deseos
            const userRef = ref(database, `users/${currentUser.uid}`);
            get(userRef).then((snapshot) => {
                if (snapshot.exists() && snapshot.val().wishListId) {
                    setHasWishList(true);
                }
            });

            // Obtener invitaciones enviadas
            const invitationsRef = ref(database, 'sharedLists');
            onValue(invitationsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const invitationList = Object.entries(data)
                        .filter(([_, value]) => value.owners.includes(currentUser.uid))
                        .map(([key, value]) => ({ id: key, ...value }));
                    setInvitations(invitationList);
                }
            });
        }
    }, []);

    const inviteCollaborator = async () => {
        const currentUser = auth.currentUser;
        if (currentUser && hasWishList) {
            try {
                const userRef = ref(database, `users/${currentUser.uid}`);
                const userSnapshot = await get(userRef);
                const wishListId = userSnapshot.val().wishListId;

                const newCollaboratorRef = push(ref(database, `wishLists/${wishListId}/collaborators`));
                await set(newCollaboratorRef, {
                    email: email,
                    status: 'pending'
                });
                alert('Invitación enviada a ' + email);
                setEmail('');
            } catch (error) {
                console.error('Error al enviar invitación:', error);
                alert('Error al enviar la invitación. Por favor, intenta de nuevo.');
            }
        } else if (!hasWishList) {
            alert('Debes crear una lista de deseos antes de invitar colaboradores.');
        } else {
            alert('Debes estar autenticado para enviar invitaciones.');
        }
    };

    return (
        <div className="page">
            <h2 className="page-title">Invitar Colaborador</h2>
            {hasWishList ? (
                <div className="invite-form">
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email del colaborador"
                        className="input"
                    />
                    <button onClick={inviteCollaborator} className="button">
                        Enviar Invitación
                    </button>
                </div>
            ) : (
                <p>Debes crear una lista de deseos antes de invitar colaboradores.</p>
            )}
            <div className="invitations-sent-section">
                <h3 className="section-title">Invitaciones enviadas:</h3>
                {invitations.length === 0 ? (
                    <p className="no-invitations">No has enviado invitaciones aún.</p>
                ) : (
                    <ul className="invitation-list">
                        {invitations.map((invitation) => (
                            <li key={invitation.id} className="invitation-item">
                                <span className="invitation-to">Invitación enviada a: {invitation.invitedEmail}</span>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default Collaborate;
