import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes, Navigate } from 'react-router-dom';
import { MapPin, Calendar, List, LogOut } from 'lucide-react';
import './App.css';

// SimulaciÃ³n de base de datos en tiempo real
const useRealTimeDatabase = (key, initialValue) => {
  const [value, setValue] = useState(() => {
    const storedValue = localStorage.getItem(key);
    return storedValue ? JSON.parse(storedValue) : initialValue;
  });

  useEffect(() => {
    const handleStorageChange = () => {
      const newValue = JSON.parse(localStorage.getItem(key));
      setValue(newValue);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, [key]);

  const updateValue = (newValue) => {
    setValue(newValue);
    localStorage.setItem(key, JSON.stringify(newValue));
  };

  return [value, updateValue];
};

// Componente de autenticaciÃ³n
const Auth = ({ setUser }) => {
  const [email, setEmail] = useState('');
  const [partnerEmail, setPartnerEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setUser({ email, partnerEmail });
    localStorage.setItem('user', JSON.stringify({ email, partnerEmail }));
  };

  return (
    <div className="page">
      <h2 className="page-title">Login</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <input
          type="email"
          placeholder="Your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="input"
          required
        />
        <input
          type="email"
          placeholder="Your partner's email"
          value={partnerEmail}
          onChange={(e) => setPartnerEmail(e.target.value)}
          className="input"
          required
        />
        <button type="submit" className="button">Login</button>
      </form>
    </div>
  );
};

// WishList component
const WishList = () => {
  const [events, setEvents] = useRealTimeDatabase('events', []);
  const [newEvent, setNewEvent] = useState({ title: '', date: '', location: '' });

  const addEvent = (e) => {
    e.preventDefault();
    setEvents([...events, { ...newEvent, id: Date.now() }]);
    setNewEvent({ title: '', date: '', location: '' });
  };

  const deleteEvent = (id) => {
    setEvents(events.filter(event => event.id !== id));
  };

  return (
    <div className="page">
      <h2 className="page-title">Wish List</h2>
      <form onSubmit={addEvent} className="event-form">
        <input
          type="text"
          placeholder="Event title"
          value={newEvent.title}
          onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
          className="input"
          required
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
          className="input"
          required
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
          className="input"
          required
        />
        <button type="submit" className="button">Add Event</button>
      </form>
      <ul className="event-list">
        {events.map((event) => (
          <li key={event.id} className="event-item">
            <strong>{event.title}</strong> - {event.date} at {event.location}
            <button onClick={() => deleteEvent(event.id)} className="delete-button">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Map component
const Map = () => {
  const [events] = useRealTimeDatabase('events', []);
  const nextEvent = events.find(event => new Date(event.date) >= new Date());

  return (
    <div className="page">
      <h2 className="page-title">Map</h2>
      {nextEvent ? (
        <div className="map-container">
          <iframe
            width="100%"
            height="450"
            frameBorder="0" style={{ border: 0 }}
            src={`https://www.google.com/maps/embed/v1/place?key=YOUR_API_KEY&q=${encodeURIComponent(nextEvent.location)}`}
            allowFullScreen>
          </iframe>
        </div>
      ) : (
        <p>No upcoming events</p>
      )}
    </div>
  );
};

// Reminders component
const Reminders = () => {
  const [events] = useRealTimeDatabase('events', []);

  const sortedEvents = events.sort((a, b) => new Date(a.date) - new Date(b.date));
  const nextEvent = sortedEvents.find(event => new Date(event.date) >= new Date());
  const pastEvents = sortedEvents.filter(event => new Date(event.date) < new Date());

  return (
    <div className="page">
      <h2 className="page-title">Reminders</h2>
      {nextEvent && (
        <div className="next-event">
          <h3>Next Event</h3>
          <p>{nextEvent.title} - {nextEvent.date} at {nextEvent.location}</p>
        </div>
      )}
      <h3>Past Events</h3>
      <ul className="event-list">
        {pastEvents.map((event) => (
          <li key={event.id} className="event-item">
            {event.title} - {event.date} at {event.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

// Main App component
const App = () => {
  const [user, setUser] = useState(() => {
    const storedUser = localStorage.getItem('user');
    return storedUser ? JSON.parse(storedUser) : null;
  });

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  if (!user) {
    return <Auth setUser={setUser} />;
  }

  return (
    <Router>
      <div className="app">
        <Routes>
          <Route path="/" element={<WishList />} />
          <Route path="/map" element={<Map />} />
          <Route path="/reminders" element={<Reminders />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
        <nav className="nav">
          <ul>
            <li>
              <Link to="/" className="nav-link">
                <List size={24} />
                <span>Wish List</span>
              </Link>
            </li>
            <li>
