import React, { useState, useEffect } from 'react';
import { database, auth } from '../services/firebase';
import { ref, push, remove, onValue, set, get } from 'firebase/database';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import { OpenStreetMapProvider } from 'leaflet-geosearch';
import 'leaflet/dist/leaflet.css';
import 'leaflet-geosearch/dist/geosearch.css';
import '../App.css';

const categories = ["Viajes", "Restaurantes", "Experiencias", "Películas y Series", "Eventos", "Regalos Mutuos", "Hobbies y Proyectos", "Deportes y Aventura", "Mejoras del Hogar", "Lectura"];
const provider = new OpenStreetMapProvider();

const WishList = () => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ 
        title: '', 
        date: new Date().toISOString().split('T')[0],
        location: '', 
        category: 'Other',
        coordinates: [51.505, -0.09]
    });
    const [showMap, setShowMap] = useState(false);
    const [searchResults, setSearchResults] = useState([]);
    const [error, setError] = useState(null);
    const [sharedListId, setSharedListId] = useState(null);

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            // Buscar lista compartida
            const sharedListsRef = ref(database, 'sharedLists');
            onValue(sharedListsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const sharedList = Object.entries(data).find(([_, value]) => 
                        value.accepted && value.owners.includes(currentUser.uid)
                    );
                    if (sharedList && sharedList[1].events) {
                        setSharedListId(sharedList[0]);
                        const combinedEvents = Object.entries(sharedList[1].events).flatMap(([uid, events]) => 
                            Object.entries(events || {}).map(([key, value]) => ({
                                id: key,
                                ...value,
                                owner: uid
                            }))
                        );
                        setEvents(combinedEvents);
                    } else {
                        // Si no hay lista compartida o no hay eventos, usar la lista personal
                        const eventsRef = ref(database, `users/${currentUser.uid}/events`);
                        onValue(eventsRef, (snapshot) => {
                            try {
                                const data = snapshot.val();
                                if (data) {
                                    const eventList = Object.entries(data).map(([key, value]) => ({
                                        id: key,
                                        ...value,
                                        owner: currentUser.uid
                                    }));
                                    setEvents(eventList);
                                } else {
                                    setEvents([]);
                                }
                            } catch (error) {
                                console.error('Error al leer eventos:', error);
                                setError('Error al cargar eventos. Por favor, intenta de nuevo más tarde.');
                            }
                        }, (error) => {
                            console.error('Error en la suscripción a eventos:', error);
                            setError('Error al suscribirse a eventos. Por favor, verifica tu conexión e intenta de nuevo.');
                        });
                    }
                }
            });
        }
    }, []);

    const addEvent = (e) => {
        e.preventDefault();
        const currentUser = auth.currentUser;
        if (currentUser) {
            const eventData = {
                ...newEvent,
                coordinates: newEvent.coordinates.map(coord => parseFloat(coord.toFixed(6))),
                owner: currentUser.uid
            };
            
            if (sharedListId) {
                const sharedListRef = ref(database, `sharedLists/${sharedListId}/events/${currentUser.uid}`);
                push(sharedListRef, eventData).then(() => {
                    console.log('Evento añadido con éxito a la lista compartida');
                    resetNewEvent();
                }).catch((error) => {
                    console.error('Error al añadir evento a la lista compartida:', error);
                    setError('Error al añadir evento. Por favor, intenta de nuevo.');
                });
            } else {
                const eventsRef = ref(database, `users/${currentUser.uid}/events`);
                push(eventsRef, eventData).then(() => {
                    console.log('Evento añadido con éxito a la lista personal');
                    resetNewEvent();
                }).catch((error) => {
                    console.error('Error al añadir evento a la lista personal:', error);
                    setError('Error al añadir evento. Por favor, intenta de nuevo.');
                });
            }
        }
    };

    const resetNewEvent = () => {
        setNewEvent({ 
            title: '', 
            date: new Date().toISOString().split('T')[0],
            location: '', 
            category: 'Other',
            coordinates: [51.505, -0.09]
        });
        setShowMap(false);
        setSearchResults([]);
    };

    const deleteEvent = (id, ownerId) => {
        const currentUser = auth.currentUser;
        if (currentUser && currentUser.uid === ownerId) {
            if (sharedListId) {
                const eventRef = ref(database, `sharedLists/${sharedListId}/wishlists/${currentUser.uid}/${id}`);
                remove(eventRef).then(() => {
                    console.log('Evento eliminado con éxito de la lista compartida');
                }).catch((error) => {
                    console.error('Error al eliminar evento de la lista compartida:', error);
                    setError('Error al eliminar evento. Por favor, intenta de nuevo.');
                });
            } else {
                const eventRef = ref(database, `users/${currentUser.uid}/events/${id}`);
                remove(eventRef).then(() => {
                    console.log('Evento eliminado con éxito de la lista personal');
                }).catch((error) => {
                    console.error('Error al eliminar evento de la lista personal:', error);
                    setError('Error al eliminar evento. Por favor, intenta de nuevo.');
                });
            }
        } else {
            alert('No tienes permiso para eliminar este evento.');
        }
    };

    const handleLocationSearch = async (query) => {
        const results = await provider.search({ query });
        setSearchResults(results);
    };

    const selectLocation = (result) => {
        setNewEvent(prev => ({
        ...prev,
        location: result.label,
        coordinates: [result.y, result.x]
        }));
        setSearchResults([]);
        setShowMap(true);
    };

    const LocationPicker = () => {
        useMapEvents({
        click(e) {
            setNewEvent(prev => ({ ...prev, coordinates: [e.latlng.lat, e.latlng.lng] }));
        },
        });

        return (
        <Marker position={newEvent.coordinates}>
            <Popup>{newEvent.location || 'Selected location'}</Popup>
        </Marker>
        );
    };

    return (
        <div className="page">
            <h2 className="page-title">Lista de Deseos {sharedListId ? 'Compartida' : 'Personal'}</h2>
            {error && <div className="error">{error}</div>}
            <form onSubmit={addEvent} className="event-form">
                <input
                type="text"
                placeholder="Título del evento"
                value={newEvent.title}
                onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                className="input"
                required
                />
                <input
                type="date"
                value={newEvent.date}
                onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
                className="input"
                required
                />
                <input
                type="text"
                placeholder="Ubicación"
                value={newEvent.location}
                onChange={(e) => {
                    setNewEvent({ ...newEvent, location: e.target.value });
                    handleLocationSearch(e.target.value);
                }}
                className="input"
                />
                {searchResults.length > 0 && (
                <ul className="search-results">
                    {searchResults.map((result, index) => (
                    <li key={index} onClick={() => selectLocation(result)}>
                        {result.label}
                    </li>
                    ))}
                </ul>
                )}
                <select
                value={newEvent.category}
                onChange={(e) => setNewEvent({ ...newEvent, category: e.target.value })}
                className="input"
                >
                {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                ))}
                </select>
                <button type="submit" className="button">Añadir Evento</button>
            </form>
            {showMap && (
                <div style={{ height: '300px', marginBottom: '20px' }}>
                <MapContainer center={newEvent.coordinates} zoom={13} style={{ height: '100%', width: '100%' }}>
                    <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                    <LocationPicker />
                </MapContainer>
                </div>
            )}
            <ul className="event-list">
            {events.map((event) => (
                <li key={event.id} className="event-item">
                    <div className="event-item-content">
                    <div className="event-item-title">{event.title}</div>
                    <div className="event-item-details">
                        {new Date(event.date).toLocaleDateString()} en {event.location}
                        <br />
                        Categoría: {event.category}
                        <br />
                        Añadido por: {event.owner === auth.currentUser.uid ? 'Tú' : 'Colaborador'}
                    </div>
                    </div>
                    {event.owner === auth.currentUser.uid && (
                        <button onClick={() => deleteEvent(event.id, event.owner)} className="delete-button">Eliminar</button>
                    )}
                </li>
                ))}
            </ul>
        </div>
    );
};

export default WishList;
