    import React, { useState } from 'react';
    import { useNavigate } from 'react-router-dom';
    import { useApp } from '../context/AppContext';

    const NewWishPage = () => {
    const navigate = useNavigate();
    const { addWish } = useApp();
    const [wishData, setWishData] = useState({
        list: '',
        title: '',
        description: '',
        photo: null,
        targetDate: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setWishData(prev => ({ ...prev, [name]: value }));
    };

    const handlePhotoChange = (e) => {
            const file = e.target.files[0];
            if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setWishData(prev => ({ ...prev, photo: reader.result }));
            };
            reader.readAsDataURL(file);
            }
        };

    const handleSubmit = (e) => {
        e.preventDefault();
        addWish(wishData);
        navigate('/');
        };

    return (
        <div className="relative flex size-full min-h-screen flex-col bg-white group/design-root overflow-x-hidden" style={{ fontFamily: 'Manrope, "Noto Sans", sans-serif' }}>
        <div className="layout-container flex h-full grow flex-col">
            <div className="px-40 flex flex-1 justify-center py-5">
            <div className="layout-content-container flex flex-col max-w-[960px] flex-1">
                <form onSubmit={handleSubmit}>
                <div className="flex flex-wrap justify-between gap-3 p-4">
                    <p className="text-[#111418] tracking-light text-[32px] font-bold leading-tight min-w-72">New Wish</p>
                </div>
                <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
                    <label className="flex flex-col min-w-40 flex-1">
                    <p className="text-[#111418] text-base font-medium leading-normal pb-2">List</p>
                    <input
                        name="list"
                        value={wishData.list}
                        onChange={handleInputChange}
                        placeholder="Create a new list"
                        className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-xl text-[#111418] focus:outline-0 focus:ring-0 border-none bg-[#f0f2f4] focus:border-none h-14 placeholder:text-[#637588] p-4 text-base font-normal leading-normal"
                    />
                    </label>
                </div>
                <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
                    <label className="flex flex-col min-w-40 flex-1">
                    <p className="text-[#111418] text-base font-medium leading-normal pb-2">Title</p>
                    <input
                        name="title"
                        value={wishData.title}
                        onChange={handleInputChange}
                        placeholder="Add a title"
                        className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-xl text-[#111418] focus:outline-0 focus:ring-0 border-none bg-[#f0f2f4] focus:border-none h-14 placeholder:text-[#637588] p-4 text-base font-normal leading-normal"
                    />
                    </label>
                </div>
                <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
                    <label className="flex flex-col min-w-40 flex-1">
                    <p className="text-[#111418] text-base font-medium leading-normal pb-2">Description</p>
                    <textarea
                        name="description"
                        value={wishData.description}
                        onChange={handleInputChange}
                        placeholder="Add a description"
                        className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-xl text-[#111418] focus:outline-0 focus:ring-0 border-none bg-[#f0f2f4] focus:border-none min-h-36 placeholder:text-[#637588] p-4 text-base font-normal leading-normal"
                    ></textarea>
                    </label>
                </div>
                <div className="flex gap-4 bg-white px-4 py-3">
                    <div className="text-[#111418] flex items-center justify-center rounded-lg bg-[#f0f2f4] shrink-0 size-12">
                    <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                        <path d="M208,56H180.28L166.65,35.56A8,8,0,0,0,160,32H96a8,8,0,0,0-6.65,3.56L75.71,56H48A24,24,0,0,0,24,80V192a24,24,0,0,0,24,24H208a24,24,0,0,0,24-24V80A24,24,0,0,0,208,56Zm8,136a8,8,0,0,1-8,8H48a8,8,0,0,1-8-8V80a8,8,0,0,1,8-8H80a8,8,0,0,0,6.66-3.56L100.28,48h55.43l13.63,20.44A8,8,0,0,0,176,72h32a8,8,0,0,1,8,8ZM128,88a44,44,0,1,0,44,44A44.05,44.05,0,0,0,128,88Zm0,72a28,28,0,1,1,28-28A28,28,0,0,1,128,160Z"></path>
                    </svg>
                    </div>
                    <div className="flex flex-1 flex-col justify-center">
                    <p className="text-[#111418] text-base font-medium leading-normal">Photo</p>
                    <input
                        type="file"
                        name="photo"
                        onChange={handlePhotoChange}
                        className="text-[#637588] text-sm font-normal leading-normal"
                    />
                    </div>
                </div>
                <div className="flex items-center gap-4 bg-white px-4 min-h-[72px] py-2">
                    <div className="text-[#111418] flex items-center justify-center rounded-lg bg-[#f0f2f4] shrink-0 size-12">
                    <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                        <path d="M208,32H184V24a8,8,0,0,0-16,0v8H88V24a8,8,0,0,0-16,0v8H48A16,16,0,0,0,32,48V208a16,16,0,0,0,16,16H208a16,16,0,0,0,16-16V48A16,16,0,0,0,208,32ZM72,48v8a8,8,0,0,0,16,0V48h80v8a8,8,0,0,0,16,0V48h24V80H48V48ZM208,208H48V96H208V208Zm-96-88v64a8,8,0,0,1-16,0V132.94l-4.42,2.22a8,8,0,0,1-7.16-14.32l16-8A8,8,0,0,1,112,120Zm59.16,30.45L152,176h16a8,8,0,0,1,0,16H136a8,8,0,0,1-6.4-12.8l28.78-38.37A8,8,0,1,0,145.07,132a8,8,0,1,1-13.85-8A24,24,0,0,1,176,136,23.76,23.76,0,0,1,171.16,150.45Z"></path>
                    </svg>
                    </div>
                    <div className="flex flex-col justify-center">
                    <p className="text-[#111418] text-base font-medium leading-normal line-clamp-1">Target date</p>
                    <input
                        type="date"
                        name="targetDate"
                        value={wishData.targetDate}
                        onChange={handleInputChange}
                        className="text-[#637588] text-sm font-normal leading-normal"
                    />
                    </div>
                </div>
                <div className="flex px-4 py-3">
                    <button type="submit" className="flex min-w-[84px] max-w-[480px] cursor-pointer items-center justify-center overflow-hidden rounded-xl h-12 px-5 flex-1 bg-[#1980e6] text-white text-base font-bold leading-normal tracking-[0.015em]">
                    <span className="truncate">Save</span>
                    </button>
                </div>
                </form>
            </div>
            </div>
        </div>
        </div>
    );
    };

    export default NewWishPage;
