    import React, { useState, useEffect } from 'react';
    import { database, auth } from '../services/firebase';
    import { ref, onValue, push, set, remove } from 'firebase/database';
    import DatePicker from "react-datepicker";
    import "react-datepicker/dist/react-datepicker.css";

    const Reminders = () => {
    const [events, setEvents] = useState([]);
    const [completedEvents, setCompletedEvents] = useState([]);
    const [deletedEvents, setDeletedEvents] = useState([]);
    const [newReminder, setNewReminder] = useState({ title: '', date: new Date(), completed: false });
    const [sharedListId, setSharedListId] = useState(null);

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const sharedListsRef = ref(database, 'sharedLists');
            const unsubscribe = onValue(sharedListsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const sharedList = Object.entries(data).find(([_, value]) => 
                        value.accepted && value.owners.includes(currentUser.uid)
                    );
                    if (sharedList) {
                        setSharedListId(sharedList[0]);
                        if (sharedList[1].events) {
                            const sharedEvents = Object.entries(sharedList[1].events).flatMap(([uid, events]) => 
                                Object.values(events || {}).map(event => ({
                                    ...event,
                                    owner: uid,
                                    id: event.id || Math.random().toString(36).substr(2, 9)
                                }))
                            );
                            setEvents(sharedEvents);
                        } else {
                            setEvents([]);
                        }
                    } else {
                        loadPersonalEvents(currentUser.uid);
                    }
                } else {
                    loadPersonalEvents(currentUser.uid);
                }
            });
    
            return () => unsubscribe();
        }
    }, []);
    
    const loadPersonalEvents = (userId) => {
        const eventsRef = ref(database, `users/${userId}/events`);
        onValue(eventsRef, (snapshot) => {
            const data = snapshot.val();
            if (data) {
                const eventList = Object.values(data);
                setEvents(eventList);
            } else {
                setEvents([]);
            }
        });
    };

    const addReminder = (e) => {
        e.preventDefault();
        const currentUser = auth.currentUser;
        if (currentUser) {
            const newEvent = {
                ...newReminder,
                id: Math.random().toString(36).substr(2, 9),
                owner: currentUser.uid,
                date: newReminder.date.toISOString(),
                completed: false
            };
            
            const eventRef = sharedListId
                ? ref(database, `sharedLists/${sharedListId}/events/${currentUser.uid}`)
                : ref(database, `users/${currentUser.uid}/events`);
            
            push(eventRef, newEvent);
            setNewReminder({ title: '', date: new Date(), completed: false });
        }
    };

    const toggleCompleted = (event) => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const eventRef = sharedListId
                ? ref(database, `sharedLists/${sharedListId}/events/${event.owner}/${event.id}`)
                : ref(database, `users/${currentUser.uid}/events/${event.id}`);
            
            const updatedEvent = { ...event, completed: !event.completed };
            set(eventRef, updatedEvent);
            
            if (updatedEvent.completed) {
                setCompletedEvents([...completedEvents, updatedEvent]);
                setEvents(events.filter(e => e.id !== event.id));
            } else {
                setEvents([...events, updatedEvent]);
                setCompletedEvents(completedEvents.filter(e => e.id !== event.id));
            }
        }
    };
    
    const deleteReminder = (event) => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const eventRef = sharedListId
                ? ref(database, `sharedLists/${sharedListId}/events/${event.owner}/${event.id}`)
                : ref(database, `users/${currentUser.uid}/events/${event.id}`);
            
            remove(eventRef);
            setDeletedEvents([...deletedEvents, event]);
            setEvents(events.filter(e => e.id !== event.id));
            setCompletedEvents(completedEvents.filter(e => e.id !== event.id));
        }
    };

const sortedEvents = events.sort((a, b) => new Date(a.date) - new Date(b.date));
const nextEvents = sortedEvents.filter(event => !event.completed && new Date(event.date) >= new Date());
const pastEvents = sortedEvents.filter(event => event.completed || new Date(event.date) < new Date());
    
return (
    <div className="ios-page">
        <h2 className="ios-page-title">Recordatorios</h2>
        <form onSubmit={addReminder} className="ios-form">
            <input
                type="text"
                value={newReminder.title}
                onChange={(e) => setNewReminder({...newReminder, title: e.target.value})}
                placeholder="Título del recordatorio"
                className="ios-input"
                required
            />
            <DatePicker
                selected={newReminder.date}
                onChange={(date) => setNewReminder({...newReminder, date})}
                dateFormat="dd/MM/yyyy"
                className="ios-datepicker"
            />
            <button type="submit" className="ios-button">Agregar Recordatorio</button>
        </form>
        <div className="ios-section">
            <h3 className="ios-section-title">Próximos Recordatorios</h3>
            <ul className="ios-list">
                {events.map((event) => (
                    <li key={event.id} className="ios-list-item">
                        <div className="ios-list-item-content">
                            <div className="ios-list-item-title">{event.title}</div>
                            <div className="ios-list-item-details">
                                <span className="ios-list-item-date">{new Date(event.date).toLocaleDateString()}</span>
                            </div>
                        </div>
                        <div className="ios-list-item-actions">
                            <button onClick={() => toggleCompleted(event)} className="ios-button-icon ios-button-complete" aria-label="Completar">
                                ✓
                            </button>
                            <button onClick={() => deleteReminder(event)} className="ios-button-icon ios-button-delete" aria-label="Eliminar">
                                ×
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
        {completedEvents.length > 0 && (
            <div className="ios-section">
                <h3 className="ios-section-title">Recordatorios Completados</h3>
                <ul className="ios-list">
                    {completedEvents.map((event) => (
                        <li key={event.id} className="ios-list-item">
                            <div className="ios-list-item-content">
                                <div className="ios-list-item-title">{event.title}</div>
                                <div className="ios-list-item-details">
                                    <span className="ios-list-item-date">{new Date(event.date).toLocaleDateString()}</span>
                                </div>
                            </div>
                            <div className="ios-list-item-actions">
                                <button onClick={() => toggleCompleted(event)} className="ios-button-small">
                                    Desmarcar
                                </button>
                                <button onClick={() => deleteReminder(event)} className="ios-button-small ios-button-danger">
                                    Eliminar
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        )}
        {deletedEvents.length > 0 && (
            <div className="ios-section">
                <h3 className="ios-section-title">Recordatorios Eliminados</h3>
                <ul className="ios-list">
                    {deletedEvents.map((event) => (
                        <li key={event.id} className="ios-list-item">
                            <div className="ios-list-item-content">
                                <div className="ios-list-item-title">{event.title}</div>
                                <div className="ios-list-item-details">
                                    <span className="ios-list-item-date">{new Date(event.date).toLocaleDateString()}</span>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        )}
    </div>
    );
    };

    export default Reminders;
