    import React, { useState } from 'react';
    import '../styles/WishForm.css';

    const WishForm = ({ addWish }) => {
    const [wish, setWish] = useState({
        title: '',
        description: '',
        category: '',
        location: '',
        latitude: '',
        longitude: '',
        date: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setWish({ ...wish, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        addWish(wish);
        setWish({
        title: '',
        description: '',
        category: '',
        location: '',
        latitude: '',
        longitude: '',
        date: ''
        });
    };

    return (
        <form className="wish-form" onSubmit={handleSubmit}>
        <h2>Agregar Nuevo Deseo</h2>
        <input
            type="text"
            name="title"
            value={wish.title}
            onChange={handleChange}
            placeholder="Título del deseo"
            required
        />
        <textarea
            name="description"
            value={wish.description}
            onChange={handleChange}
            placeholder="Descripción"
            required
        />
        <select
            name="category"
            value={wish.category}
            onChange={handleChange}
            required
        >
            <option value="">Selecciona una categoría</option>
            <option value="viajes">Viajes</option>
            <option value="comidas">Comidas</option>
            <option value="actividades">Actividades</option>
            <option value="otros">Otros</option>
        </select>
        <input
            type="text"
            name="location"
            value={wish.location}
            onChange={handleChange}
            placeholder="Ubicación"
            required
        />
        <input
            type="number"
            name="latitude"
            value={wish.latitude}
            onChange={handleChange}
            placeholder="Latitud"
            step="any"
            required
        />
        <input
            type="number"
            name="longitude"
            value={wish.longitude}
            onChange={handleChange}
            placeholder="Longitud"
            step="any"
            required
        />
        <input
            type="date"
            name="date"
            value={wish.date}
            onChange={handleChange}
            required
        />
        <button type="submit">Agregar Deseo</button>
        </form>
    );
    };

    export default WishForm;
