    import React, { useState, useEffect } from 'react';
    import { database, auth } from '../services/firebase';
    import { ref, push, set, onValue } from 'firebase/database';

    const Collaborate = () => {
    const [email, setEmail] = useState('');
    const [invitations, setInvitations] = useState([]);

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const invitationsRef = ref(database, 'sharedLists');
            onValue(invitationsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const invitationList = Object.entries(data)
                        .filter(([_, value]) => value.owners.includes(currentUser.uid))
                        .map(([key, value]) => ({ id: key, ...value }));
                    setInvitations(invitationList);
                }
            });
        }
    }, []);

    const inviteCollaborator = async () => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            try {
                const newListRef = push(ref(database, 'sharedLists'));
                await set(newListRef, {
                    owners: [currentUser.uid],
                    invitedEmail: email
                });
                alert('Invitación enviada');
                setEmail('');
            } catch (error) {
                console.error('Error al enviar invitación:', error);
                alert('Error al enviar invitación. Por favor, intenta de nuevo.');
            }
        }
    };

    return (
        <div className="page">
        <h2 className="page-title">Invitar Colaborador</h2>
        <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email del colaborador"
            className="input"
        />
        <button onClick={inviteCollaborator} className="button">
            Enviar Invitación
        </button>
        <h3>Invitaciones enviadas:</h3>
        <ul>
            {invitations.map((invitation) => (
                <li key={invitation.id}>
                    Invitación enviada a: {invitation.invitedEmail}
                </li>
            ))}
        </ul>
        </div>
    );
    };

    export default Collaborate;
