import React, { useState, useEffect } from 'react';
import { database, auth } from '../services/firebase';
import { ref, onValue, push, remove, update } from 'firebase/database';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { FaCheck, FaTimes, FaUndo, FaCalendarAlt, FaHourglassHalf, FaCheckCircle } from 'react-icons/fa';
import IOSAlert from './IOSAlert';

const Reminders = () => {
    const [events, setEvents] = useState([]);
    const [completedEvents, setCompletedEvents] = useState([]);
    const [deletedEvents, setDeletedEvents] = useState([]);
    const [newReminder, setNewReminder] = useState({ title: '', date: null, completed: false });
    const [sharedListId, setSharedListId] = useState(null);
    const [isAlertOpen, setIsAlertOpen] = useState(false);
    const [alertConfig, setAlertConfig] = useState({ title: '', message: '', onConfirm: () => {} });

    useEffect(() => {
        const currentUser = auth.currentUser;
        if (currentUser) {
            const sharedListsRef = ref(database, 'sharedLists');
            const unsubscribe = onValue(sharedListsRef, (snapshot) => {
                const data = snapshot.val();
                if (data) {
                    const sharedList = Object.entries(data).find(([_, value]) => 
                        value.accepted && value.owners.includes(currentUser.uid)
                    );
                    if (sharedList) {
                        setSharedListId(sharedList[0]);
                        loadSharedEvents(sharedList[0]);
                    } else {
                        setSharedListId(null);
                        loadPersonalEvents(currentUser.uid);
                    }
                } else {
                    setSharedListId(null);
                    loadPersonalEvents(currentUser.uid);
                }
            });
    
            return () => unsubscribe();
        }
    }, []);
    
    const loadPersonalEvents = (userId) => {
        const eventsRef = ref(database, `users/${userId}/events`);
        onValue(eventsRef, (snapshot) => {
            const data = snapshot.val();
            if (data) {
                const eventList = Object.entries(data).map(([id, event]) => ({
                    ...event,
                    id,
                    owner: userId,
                    isPersonal: true
                }));
                setEvents(eventList.filter(event => !event.completed));
                setCompletedEvents(eventList.filter(event => event.completed));
            } else {
                setEvents([]);
                setCompletedEvents([]);
            }
        });
    };
    
    const loadSharedEvents = (listId) => {
        const sharedEventsRef = ref(database, `sharedLists/${listId}/events`);
        onValue(sharedEventsRef, (snapshot) => {
            const data = snapshot.val();
            if (data) {
                const eventList = Object.entries(data).flatMap(([ownerId, ownerEvents]) =>
                    Object.entries(ownerEvents || {}).map(([id, event]) => ({
                        ...event,
                        id,
                        owner: ownerId,
                        isShared: true
                    }))
                );
                setEvents(eventList.filter(event => !event.completed));
                setCompletedEvents(eventList.filter(event => event.completed));
            } else {
                setEvents([]);
                setCompletedEvents([]);
            }
        });
    };

    const addReminder = (e) => {
        e.preventDefault();
        const currentUser = auth.currentUser;
        if (currentUser) {
            const newEvent = {
                ...newReminder,
                owner: currentUser.uid,
                date: newReminder.date.toISOString(),
                completed: false
            };
            
            const eventRef = sharedListId
                ? ref(database, `sharedLists/${sharedListId}/events/${currentUser.uid}`)
                : ref(database, `users/${currentUser.uid}/events`);
            
            push(eventRef, newEvent);
            setNewReminder({ title: '', date: new Date(), completed: false });
        }
    };

    const toggleCompleted = (event) => {
        const action = event.completed ? "desmarcar" : "marcar como completado";
        setAlertConfig({
            title: `${action.charAt(0).toUpperCase() + action.slice(1)} recordatorio`,
            message: `¿Estás seguro que quieres ${action} este recordatorio?`,
            onConfirm: () => {
                const currentUser = auth.currentUser;
                if (currentUser) {
                    const eventRef = sharedListId
                        ? ref(database, `sharedLists/${sharedListId}/events/${event.owner}/${event.id}`)
                        : ref(database, `users/${currentUser.uid}/events/${event.id}`);
                    
                    const updatedEvent = { ...event, completed: !event.completed };
                    update(eventRef, updatedEvent);
                }
                setIsAlertOpen(false);
            }
        });
        setIsAlertOpen(true);
    };

    const deleteReminder = (event) => {
        setAlertConfig({
            title: "Eliminar recordatorio",
            message: "¿Estás seguro que quieres eliminar este recordatorio?",
            onConfirm: () => {
                const currentUser = auth.currentUser;
                if (currentUser) {
                    const eventRef = sharedListId
                        ? ref(database, `sharedLists/${sharedListId}/events/${event.owner}/${event.id}`)
                        : ref(database, `users/${currentUser.uid}/events/${event.id}`);
                    
                    remove(eventRef);
                }
                setIsAlertOpen(false);
            }
        });
        setIsAlertOpen(true);
    };

    const sortedEvents = events.sort((a, b) => new Date(a.date) - new Date(b.date));
    const nextEvents = sortedEvents.filter(event => !event.completed && new Date(event.date) >= new Date());
    const pastEvents = sortedEvents.filter(event => !event.completed && new Date(event.date) < new Date());
    
    return (
        <div className="ios-page">
            <h2 className="ios-page-title">Recordatorios</h2>
            <form onSubmit={addReminder} className="ios-form">
                <input
                    type="text"
                    value={newReminder.title}
                    onChange={(e) => setNewReminder({...newReminder, title: e.target.value})}
                    placeholder="Título del recordatorio"
                    className="ios-input"
                    required
                />
                <DatePicker
                    selected={newReminder.date}
                    onChange={(date) => setNewReminder({...newReminder, date})}
                    dateFormat="dd/MM/yyyy"
                    className="ios-datepicker"
                    placeholderText="Seleccionar fecha"
                    isClearable
                />
                <button type="submit" className="ios-button">Agregar Recordatorio</button>
            </form>
            <div className="ios-section">
                <ul className="ios-list">
                    {nextEvents.map((event) => (
                        <li key={event.id} className="ios-list-item">
                            <div className="ios-list-item-content">
                                <div className="ios-list-item-title">{event.title}</div>
                                <div className="ios-list-item-details">
                                    <span className="ios-list-item-date">{new Date(event.date).toLocaleDateString()}</span>
                                </div>
                            </div>
                            <div className="ios-list-item-actions">
                                <button onClick={() => toggleCompleted(event)} className="ios-button-icon ios-button-complete" aria-label="Completar">
                                    <FaCheck />
                                </button>
                                <button onClick={() => deleteReminder(event)} className="ios-button-icon ios-button-delete" aria-label="Eliminar">
                                    <FaTimes />
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            {pastEvents.length > 0 && (
                <div className="ios-section">
                    <h3 className="ios-section-title">
                        Próximos Recordatorios <FaCalendarAlt className="ios-section-icon" />
                    </h3>
                    <ul className="ios-list">
                        {pastEvents.map((event) => (
                            <li key={event.id} className="ios-list-item">
                                <div className="ios-list-item-content">
                                    <div className="ios-list-item-title">{event.title}</div>
                                    <div className="ios-list-item-details">
                                        <span className="ios-list-item-date">{new Date(event.date).toLocaleDateString()}</span>
                                    </div>
                                </div>
                                <div className="ios-list-item-actions">
                                    <button onClick={() => toggleCompleted(event)} className="ios-button-icon ios-button-complete" aria-label="Completar">
                                        <FaCheck />
                                    </button>
                                    <button onClick={() => deleteReminder(event)} className="ios-button-icon ios-button-delete" aria-label="Eliminar">
                                        <FaTimes />
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            {completedEvents.length > 0 && (
                <div className="ios-section">
                    <h3 className="ios-section-title">
                        Recordatorios Completados <FaCheckCircle className="ios-section-icon" />
                    </h3>
                    <ul className="ios-list">
                        {completedEvents.map((event) => (
                            <li key={event.id} className="ios-list-item">
                                <div className="ios-list-item-content">
                                    <div className="ios-list-item-title">{event.title}</div>
                                    <div className="ios-list-item-details">
                                        <span className="ios-list-item-date">{new Date(event.date).toLocaleDateString()}</span>
                                    </div>
                                </div>
                                <div className="ios-list-item-actions">
                                    <button onClick={() => toggleCompleted(event)} className="ios-button-icon ios-button-undo" aria-label="Desmarcar">
                                        <FaUndo />
                                    </button>
                                    <button onClick={() => deleteReminder(event)} className="ios-button-icon ios-button-delete" aria-label="Eliminar">
                                        <FaTimes />
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            <IOSAlert
                isOpen={isAlertOpen}
                onClose={() => setIsAlertOpen(false)}
                onConfirm={alertConfig.onConfirm}
                title={alertConfig.title}
                message={alertConfig.message}
            />
        </div>
    );
};

export default Reminders;
