// Simulamos una base de datos con un array
let activities = [
    { id: 1, title: 'Juego de Vajilla de Cerámica', addedBy: 'Lindsay', daysAgo: 2, image: 'ruta_a_imagen_ceramica.jpg' },
    { id: 2, title: 'Carpa de Camping', addedBy: 'Jeff', daysAgo: 5, image: 'ruta_a_imagen_carpa.jpg' },
    { id: 3, title: 'Saco de Dormir', addedBy: 'Jeff', daysAgo: 5, image: 'ruta_a_imagen_saco.jpg' },
    { id: 4, title: 'Linterna LED', addedBy: 'Lindsay', daysAgo: 3, image: 'ruta_a_imagen_linterna.jpg' },
    { id: 5, title: 'Set de Cocina para Camping', addedBy: 'Jeff', daysAgo: 1, image: 'ruta_a_imagen_cocina.jpg' },
];

// Función para obtener todas las actividades
export const getActivities = () => {
    return new Promise((resolve) => {
    setTimeout(() => {
        resolve(activities);
    }, 500); // Simulamos un retraso de red de 500ms
    });
};

// Función para agregar una nueva actividad
export const addActivity = (newActivity) => {
    return new Promise((resolve) => {
    setTimeout(() => {
        const activity = {
        id: activities.length + 1,
        ...newActivity,
        daysAgo: 0
        };
        activities.push(activity);
        resolve(activity);
    }, 500);
    });
};

// Función para eliminar una actividad
export const deleteActivity = (id) => {
    return new Promise((resolve) => {
    setTimeout(() => {
        activities = activities.filter(activity => activity.id !== id);
        resolve({ success: true });
    }, 500);
    });
};

// Función para actualizar una actividad
export const updateActivity = (id, updatedActivity) => {
    return new Promise((resolve) => {
    setTimeout(() => {
        const index = activities.findIndex(activity => activity.id === id);
        if (index !== -1) {
        activities[index] = { ...activities[index], ...updatedActivity };
        resolve(activities[index]);
        } else {
        resolve(null);
        }
    }, 500);
    });
};
