    import React, { createContext, useContext, useState } from 'react';

    const AppContext = createContext();

    export const useApp = () => useContext(AppContext);

    export const AppProvider = ({ children }) => {
    const [wishes, setWishes] = useState([]);
    const [reminders, setReminders] = useState([]);

    const addWish = (wish) => {
        setWishes(prevWishes => [...prevWishes, { ...wish, id: Date.now() }]);
    };

    const addReminder = (reminder) => {
        setReminders(prevReminders => [...prevReminders, { ...reminder, id: Date.now(), fulfilled: false }]);
    };

    const fulfillReminder = (id) => {
        setReminders(prevReminders =>
        prevReminders.map(reminder =>
            reminder.id === id ? { ...reminder, fulfilled: true } : reminder
        )
        );
    };

    const deleteReminder = (id) => {
        setReminders(prevReminders => prevReminders.filter(reminder => reminder.id !== id));
    };

    return (
        <AppContext.Provider value={{ wishes, addWish, reminders, addReminder, fulfillReminder, deleteReminder }}>
        {children}
        </AppContext.Provider>
    );
    };
