    import React, { useState, useEffect } from 'react';
    import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
    import { database } from '../services/firebase';
    import { ref, onValue } from 'firebase/database';
    import L from 'leaflet';
    import 'leaflet/dist/leaflet.css';
    import '../App.css';

    const Map = () => {
    const [events, setEvents] = useState([]);
    const [nextEvent, setNextEvent] = useState(null);

    useEffect(() => {
        const eventsRef = ref(database, 'events');
        const unsubscribe = onValue(eventsRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
            const eventList = Object.values(data);
            setEvents(eventList);
            
            // Encontrar el próximo evento
            const sortedEvents = eventList.sort((a, b) => new Date(a.date) - new Date(b.date));
            const next = sortedEvents.find(event => new Date(event.date) >= new Date());
            setNextEvent(next);
        } else {
            setEvents([]);
            setNextEvent(null);
        }
        });

        return () => unsubscribe();
    }, []);

    // Center the map on the next event, or use a default location
    const center = nextEvent ? nextEvent.coordinates : [51.505, -0.09];

    // Icono personalizado para el próximo evento
    const nextEventIcon = new L.Icon({
        iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
        shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
        iconSize: [25, 41],
        iconAnchor: [12, 41],
        popupAnchor: [1, -34],
        shadowSize: [41, 41]
    });

    return (
        <div className="page">
        <h2 className="page-title">Mapa de Eventos</h2>
        {nextEvent && (
            <div className="next-event">
            <h3>Próximo Evento</h3>
            <p>
                <strong>{nextEvent.title}</strong><br />
                Fecha: {new Date(nextEvent.date).toLocaleDateString()}<br />
                Ubicación: {nextEvent.location}<br />
                Categoría: {nextEvent.category}
            </p>
            </div>
        )}
        <div style={{ height: '400px', width: '100%' }}>
            <MapContainer center={center} zoom={13} style={{ height: '100%', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {events.map((event, index) => (
                <Marker 
                key={index} 
                position={event.coordinates}
                icon={event === nextEvent ? nextEventIcon : new L.Icon.Default()}
                >
                <Popup>
                    <strong>{event.title}</strong><br />
                    Fecha: {new Date(event.date).toLocaleDateString()}<br />
                    Ubicación: {event.location}<br />
                    Categoría: {event.category}
                </Popup>
                </Marker>
            ))}
            </MapContainer>
        </div>
        </div>
    );
    };

    export default Map;
