    import React from 'react';
    import { Link } from 'react-router-dom';
    import { useApp } from '../context/AppContext';

    const WishesPage = () => {
    const { wishes } = useApp();

    return (
        <div className="p-8">
        <h1 className="text-2xl font-bold mb-4">Your Wishes</h1>
        {wishes.length === 0 ? (
            <p>You haven't created any wishes yet. <Link to="/new-wish" className="text-blue-500">Create a new wish</Link></p>
        ) : (
            <ul className="space-y-4">
            {wishes.map(wish => (
                <li key={wish.id} className="border p-4 rounded-lg">
                <h2 className="text-xl font-semibold">{wish.title}</h2>
                <p className="text-gray-600">{wish.description}</p>
                <p className="text-sm text-gray-500 mt-2">Target Date: {wish.targetDate}</p>
                </li>
            ))}
            </ul>
        )}
        <Link to="/new-wish" className="mt-4 inline-block bg-blue-500 text-white px-4 py-2 rounded">Create New Wish</Link>
        </div>
    );
    };

    export default WishesPage;
