import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useApp } from '../context/AppContext';

const ExplorePage = () => {
    const { wishes, loadWishes } = useApp();
    const [searchTerm, setSearchTerm] = useState('');
    const [mapSearchTerm, setMapSearchTerm] = useState('');
    const [userLists, setUserLists] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        loadWishes();
        // Aquí podrías cargar las listas del usuario si las tienes en el contexto
        // Por ahora, usaremos un ejemplo estático
        setUserLists([
            { id: 1, name: 'Mi lista de cumpleaños' },
            { id: 2, name: 'Lista de regalos para Navidad' }
        ]);
    }, [loadWishes]);

    const handleSearch = (e) => {
        e.preventDefault();
        // Aquí podrías filtrar los deseos basados en el término de búsqueda
        console.log('Buscando:', searchTerm);
    };

    const handleMapSearch = (e) => {
        e.preventDefault();
        console.log('Buscando en el mapa:', mapSearchTerm);
    };

    const handleZoomIn = () => {
        console.log('Acercando');
    };

    const handleZoomOut = () => {
        console.log('Alejando');
    };

    const handleLocate = () => {
        console.log('Localizando usuario');
    };

    return (
        <div className="relative flex size-full min-h-screen flex-col bg-[#FFFFFF] group/design-root overflow-x-hidden" style={{ fontFamily: 'Manrope, "Noto Sans", sans-serif' }}>
            <div className="layout-container flex h-full grow flex-col">
                <div className="px-40 flex flex-1 justify-center py-5">
                    <div className="layout-content-container flex flex-col max-w-[960px] flex-1">
                        <h1 className="text-black text-[22px] font-bold leading-tight tracking-[-0.015em] px-4 text-left pb-3 pt-5">Explorar</h1>
                        <div className="px-4 py-3">
                            <form onSubmit={handleSearch}>
                                <label className="flex flex-col min-w-40 h-12 w-full">
                                    <div className="flex w-full flex-1 items-stretch rounded-xl h-full">
                                        <div className="text-[#6B6B6B] flex border-none bg-[#EEEEEE] items-center justify-center pl-4 rounded-l-xl border-r-0">
                                            <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                                                <path d="M229.66,218.34l-50.07-50.06a88.11,88.11,0,1,0-11.31,11.31l50.06,50.07a8,8,0,0,0,11.32-11.32ZM40,112a72,72,0,1,1,72,72A72.08,72.08,0,0,1,40,112Z"></path>
                                            </svg>
                                        </div>
                                        <input
                                            type="text"
                                            value={searchTerm}
                                            onChange={(e) => setSearchTerm(e.target.value)}
                                            placeholder="Buscar"
                                            className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-xl text-black focus:outline-0 focus:ring-0 border-none bg-[#EEEEEE] focus:border-none h-full placeholder:text-[#6B6B6B] px-4 rounded-l-none border-l-0 pl-2 text-base font-normal leading-normal"
                                        />
                                    </div>
                                </label>
                            </form>
                        </div>
                        <div className="@container flex flex-col h-full flex-1">
                            <div className="flex flex-1 flex-col @[480px]:px-4 @[480px]:py-3">
                                <div
                                    className="bg-cover bg-center flex min-h-[320px] flex-1 flex-col justify-between px-4 pb-4 pt-5 @[480px]:rounded-xl @[480px]:px-8 @[480px]:pb-6 @[480px]:pt-8"
                                    style={{ backgroundImage: 'url("https://cdn.usegalileo.ai/maps/22b4b700-91d1-4248-b514-5790437eb725.png")' }}
                                >
                                    <form onSubmit={handleMapSearch}>
                                        <label className="flex flex-col min-w-40 h-12">
                                            <div className="flex w-full flex-1 items-stretch rounded-xl h-full">
                                                <div className="text-[#6B6B6B] flex border-none bg-[#FFFFFF] items-center justify-center pl-4 rounded-l-xl border-r-0">
                                                    <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                                                        <path d="M229.66,218.34l-50.07-50.06a88.11,88.11,0,1,0-11.31,11.31l50.06,50.07a8,8,0,0,0,11.32-11.32ZM40,112a72,72,0,1,1,72,72A72.08,72.08,0,0,1,40,112Z"></path>
                                                    </svg>
                                                </div>
                                                <input
                                                    type="text"
                                                    value={mapSearchTerm}
                                                    onChange={(e) => setMapSearchTerm(e.target.value)}
                                                    placeholder="Buscar ciudades, países o lugares"
                                                    className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-xl text-black focus:outline-0 focus:ring-0 border-none bg-[#FFFFFF] focus:border-none h-full placeholder:text-[#6B6B6B] px-4 rounded-l-none border-l-0 pl-2 text-base font-normal leading-normal"
                                                />
                                            </div>
                                        </label>
                                    </form>
                                    <div className="flex flex-col items-end gap-3">
                                        <div className="flex flex-col gap-0.5">
                                            <button onClick={handleZoomIn} className="flex size-10 items-center justify-center rounded-t-xl bg-[#FFFFFF] shadow-[0_2px_4px_rgba(0,0,0,0.1)]">
                                                <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                                                    <path d="M224,128a8,8,0,0,1-8,8H136v80a8,8,0,0,1-16,0V136H40a8,8,0,0,1,0-16h80V40a8,8,0,0,1,16,0v80h80A8,8,0,0,1,224,128Z"></path>
                                                </svg>
                                            </button>
                                            <button onClick={handleZoomOut} className="flex size-10 items-center justify-center rounded-b-xl bg-[#FFFFFF] shadow-[0_2px_4px_rgba(0,0,0,0.1)]">
                                                <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
                                                    <path d="M224,128a8,8,0,0,1-8,8H40a8,8,0,0,1,0-16H216A8,8,0,0,1,224,128Z"></path>
                                                </svg>
                                            </button>
                                        </div>
                                        <button onClick={handleLocate} className="flex size-10 items-center justify-center rounded-xl bg-[#FFFFFF] shadow-[0_2px_4px_rgba(0,0,0,0.1)]">
                                            <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256" transform="scale(-1, 1)">
                                                <path d="M229.33,98.21,53.41,33l-.16-.05A16,16,0,0,0,32.9,53.25a1,1,0,0,0,.05.16L98.21,229.33A15.77,15.77,0,0,0,113.28,240h.3a15.77,15.77,0,0,0,15-11.29l23.56-76.56,76.56-23.56a16,16,0,0,0,.62-30.38ZM224,113.3l-76.56,23.56a16,16,0,0,0-10.58,10.58L113.3,224h0l-.06-.17L48,48l175.82,65.22.16.06Z"></path>
                                            </svg>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <h2 className="text-black text-[22px] font-bold leading-tight tracking-[-0.015em] px-4 pb-3 pt-5">Tus listas</h2>
                        <div className="px-4">
                            {userLists.map((list) => (
                                <Link key={list.id} to={`/list/${list.id}`} className="block py-2 hover:bg-gray-100 rounded">
                                    {list.name}
                                </Link>
                            ))}
                            <Link to="/new-wish" className="block py-2 text-blue-500 hover:bg-gray-100 rounded">
                                + Crear nueva lista
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ExplorePage;
