import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';

const RemindersPage = () => {
    const { reminders, loadReminders, addReminder, fulfillReminder, deleteReminder } = useApp();
    const [newReminder, setNewReminder] = useState({ title: '', date: '' });

    useEffect(() => {
        loadReminders();
    }, [loadReminders]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewReminder(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (newReminder.title && newReminder.date) {
            addReminder(newReminder);
            setNewReminder({ title: '', date: '' });
        }
    };

    const upcomingReminders = reminders.filter(reminder => !reminder.fulfilled);
    const fulfilledReminders = reminders.filter(reminder => reminder.fulfilled);

    return (
        <div className="container mx-auto px-4 py-8">
            <h1 className="text-3xl font-bold mb-6">Reminders</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                    <h2 className="text-2xl font-semibold mb-4">Upcoming Reminders</h2>
                    {upcomingReminders.map(reminder => (
                        <div key={reminder.id} className="bg-white p-4 rounded-lg shadow mb-4 flex justify-between items-center">
                            <div>
                                <h3 className="font-semibold">{reminder.title}</h3>
                                <p className="text-sm text-gray-500">{reminder.date}</p>
                            </div>
                            <div>
                                <button
                                    onClick={() => fulfillReminder(reminder.id)}
                                    className="text-green-500 mr-2 hover:text-green-700"
                                >
                                    Fulfill
                                </button>
                                <button
                                    onClick={() => deleteReminder(reminder.id)}
                                    className="text-red-500 hover:text-red-700"
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))}
                    <h2 className="text-2xl font-semibold mb-4 mt-8">Fulfilled Reminders</h2>
                    {fulfilledReminders.map(reminder => (
                        <div key={reminder.id} className="bg-gray-100 p-4 rounded-lg shadow mb-4 flex justify-between items-center">
                            <div>
                                <h3 className="font-semibold">{reminder.title}</h3>
                                <p className="text-sm text-gray-500">{reminder.date}</p>
                            </div>
                            <button
                                onClick={() => deleteReminder(reminder.id)}
                                className="text-red-500 hover:text-red-700"
                            >
                                Delete
                            </button>
                        </div>
                    ))}
                </div>
                <div>
                    <h2 className="text-2xl font-semibold mb-4">Add New Reminder</h2>
                    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow">
                        <div className="mb-4">
                            <label htmlFor="title" className="block text-gray-700 font-semibold mb-2">Title</label>
                            <input
                                type="text"
                                id="title"
                                name="title"
                                value={newReminder.title}
                                onChange={handleInputChange}
                                className="w-full p-2 border rounded"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label htmlFor="date" className="block text-gray-700 font-semibold mb-2">Date</label>
                            <input
                                type="date"
                                id="date"
                                name="date"
                                value={newReminder.date}
                                onChange={handleInputChange}
                                className="w-full p-2 border rounded"
                                required
                            />
                        </div>
                        <button
                            type="submit"
                            className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
                        >
                            Add Reminder
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default RemindersPage;
