    import React, { useEffect } from 'react';
    import { Link } from 'react-router-dom';
    import { useApp } from '../context/AppContext';

    const ActivityPage = () => {
    const { wishes, loadWishes } = useApp();

    useEffect(() => {
        loadWishes();
    }, [loadWishes]);

    return (
        <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl font-bold">Lista de Deseos</h1>
            <Link to="/new-wish" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600">
            Agregar Nuevo Deseo
            </Link>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
            {wishes.map((wish) => (
                <div key={wish.id} className="flex items-center mb-4 bg-white p-4 rounded-lg shadow">
                    {wish.photo && typeof wish.photo === 'string' && (
                    <img src={wish.photo} alt={wish.title} className="w-16 h-16 object-cover rounded-lg mr-4" />
                    )}
                    <div>
                    <h2 className="text-xl font-semibold">{wish.title}</h2>
                    <p className="text-gray-600">{wish.description}</p>
                    <p className="text-sm text-gray-500">Fecha objetivo: {wish.targetDate}</p>
                    </div>
                </div>
                ))}
            </div>

            <div className="bg-gray-100 p-6 rounded-lg">
            <h2 className="text-2xl font-bold mb-4">Agregar un Deseo</h2>
            <div className="space-y-4">
                <Link to="/new-wish" className="flex items-center w-full bg-white p-3 rounded-lg hover:bg-gray-50">
                <span>Agregar nuevo deseo</span>
                </Link>
            </div>
            </div>
        </div>
        </div>
    );
    };

    export default ActivityPage;
