    import React, { useState } from 'react';
    import '../styles/NewWishForm.css';

    const NewWishForm = () => {
    const [wish, setWish] = useState({
        list: '',
        title: '',
        description: '',
        photo: '',
        targetDate: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setWish({ ...wish, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí iría la lógica para guardar el nuevo deseo
        console.log(wish);
    };

    return (
        <div className="new-wish-form">
        <h1>New Wish</h1>
        <form onSubmit={handleSubmit}>
            <div className="form-group">
            <label>List</label>
            <input type="text" name="list" value={wish.list} onChange={handleChange} placeholder="Create a new list" />
            </div>
            <div className="form-group">
            <label>Title</label>
            <input type="text" name="title" value={wish.title} onChange={handleChange} placeholder="Add a title" />
            </div>
            <div className="form-group">
            <label>Description</label>
            <textarea name="description" value={wish.description} onChange={handleChange} placeholder="Add a description"></textarea>
            </div>
            <div className="form-group">
            <label>Photo</label>
            <input type="file" name="photo" onChange={handleChange} />
            </div>
            <div className="form-group">
            <label>Target date</label>
            <input type="date" name="targetDate" value={wish.targetDate} onChange={handleChange} />
            </div>
            <button type="submit" className="save-button">Save</button>
        </form>
        </div>
    );
    };

    export default NewWishForm;
