    import React, { useState } from 'react';
    import { Link } from 'react-router-dom';
    import { MapPin, Calendar, List, User, LogOut, Users } from 'lucide-react';
    import { auth } from '../services/firebase';
    import { signOut } from 'firebase/auth';
    import IOSAlert from './IOSAlert';

    const Navigation = () => {
        const [showLogoutAlert, setShowLogoutAlert] = useState(false);

        const handleLogoutClick = () => {
            setShowLogoutAlert(true);
        };

        const handleLogoutConfirm = async () => {
            try {
                await signOut(auth);
                setShowLogoutAlert(false);
            } catch (error) {
                console.error('Error de cierre de sesión:', error);
            }
        };

        const handleLogoutCancel = () => {
            setShowLogoutAlert(false);
        };

        return (
            <>
                <nav className="nav">
                    <ul>
                        <li>
                            <Link to="/" className="nav-link">
                                <List size={24} />
                                <span>Wish List</span>
                            </Link>
                        </li>
                        <li>
                            <Link to="/map" className="nav-link">
                                <MapPin size={24} />
                                <span>Map</span>
                            </Link>
                        </li>
                        <li>
                            <Link to="/reminders" className="nav-link">
                                <Calendar size={24} />
                                <span>Reminders</span>
                            </Link>
                        </li>
                        <li>
                            <Link to="/collaborate" className="nav-link">
                                <Users size={24} />
                                <span>Colaborar</span>
                            </Link>
                        </li>
                        <li>
                            <Link to="/profile" className="nav-link">
                                <User size={24} />
                                <span>Perfil</span>
                            </Link>
                        </li>
                        <li>
                            <button onClick={handleLogoutClick} className="nav-link logout-button">
                                <LogOut size={24} />
                                <span>Logout</span>
                            </button>
                        </li>
                    </ul>
                </nav>
                <IOSAlert
                    isOpen={showLogoutAlert}
                    onClose={handleLogoutCancel}
                    onConfirm={handleLogoutConfirm}
                    title="Cerrar sesión"
                    message="¿Estás seguro de que quieres cerrar sesión?"
                />
            </>
        );
    };

    export default Navigation;
