import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useApp } from '../context/AppContext';

const ExplorePage = () => {
    const { wishes, loadWishes, deleteWish } = useApp();
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredWishes, setFilteredWishes] = useState([]);

    useEffect(() => {
        loadWishes();
    }, [loadWishes]);

    useEffect(() => {
        setFilteredWishes(
            wishes.filter(wish =>
                wish.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                wish.description.toLowerCase().includes(searchTerm.toLowerCase())
            )
        );
    }, [wishes, searchTerm]);

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <div className="container mx-auto px-4 py-8">
            <h1 className="text-3xl font-bold mb-6">Explore Wishes</h1>
            <div className="mb-6">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleSearch}
                    placeholder="Search wishes..."
                    className="w-full p-2 border rounded"
                />
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredWishes.map((wish) => (
                    <div key={wish.id} className="bg-white p-4 rounded-lg shadow">
                        <h3 className="font-semibold text-lg mb-2">{wish.title}</h3>
                        <p className="text-gray-600 mb-2">{wish.description}</p>
                        {wish.targetDate && (
                            <p className="text-sm text-gray-500 mb-2">Target Date: {wish.targetDate}</p>
                        )}
                        <div className="flex justify-between items-center">
                            <Link to={`/wish/${wish.id}`} className="text-blue-500 hover:underline">
                                View Details
                            </Link>
                            <button
                                onClick={() => deleteWish(wish.id)}
                                className="text-red-500 hover:text-red-700"
                            >
                                Delete
                            </button>
                        </div>
                    </div>
                ))}
            </div>
            <div className="mt-6">
                <Link
                    to="/new-wish"
                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                    Add New Wish
                </Link>
            </div>
        </div>
    );
};

export default ExplorePage;
