import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import { MapPin, Calendar, List } from 'lucide-react';

// WishList component
const WishList = () => {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: '', date: '', location: '' });

  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    setEvents(storedEvents);
  }, []);

  const addEvent = (e) => {
    e.preventDefault();
    const updatedEvents = [...events, newEvent];
    setEvents(updatedEvents);
    localStorage.setItem('events', JSON.stringify(updatedEvents));
    setNewEvent({ title: '', date: '', location: '' });
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Wish List</h2>
      <form onSubmit={addEvent} className="mb-4">
        <input
          type="text"
          placeholder="Event title"
          value={newEvent.title}
          onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
          className="w-full p-2 mb-2 border rounded"
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
          className="w-full p-2 mb-2 border rounded"
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
          className="w-full p-2 mb-2 border rounded"
        />
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
          Add Event
        </button>
      </form>
      <ul>
        {events.map((event, index) => (
          <li key={index} className="mb-2 p-2 bg-gray-100 rounded">
            <strong>{event.title}</strong> - {event.date} at {event.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

// Map component (placeholder)
const Map = () => {
  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Map</h2>
      <p>Map placeholder - Next event location will be shown here</p>
    </div>
  );
};

// Reminders component
const Reminders = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    setEvents(storedEvents);
  }, []);

  const sortedEvents = events.sort((a, b) => new Date(a.date) - new Date(b.date));
  const nextEvent = sortedEvents.find(event => new Date(event.date) >= new Date());
  const pastEvents = sortedEvents.filter(event => new Date(event.date) < new Date());

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Reminders</h2>
      {nextEvent && (
        <div className="mb-4">
          <h3 className="text-xl font-semibold">Next Event</h3>
          <p>{nextEvent.title} - {nextEvent.date} at {nextEvent.location}</p>
        </div>
      )}
      <h3 className="text-xl font-semibold mb-2">Past Events</h3>
      <ul>
        {pastEvents.map((event, index) => (
          <li key={index} className="mb-2">
            {event.title} - {event.date} at {event.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

// Main App component
const App = () => {
  return (
    <Router>
      <div className="max-w-md mx-auto bg-white min-h-screen">
        <Routes>
          <Route path="/" element={<WishList />} />
          <Route path="/map" element={<Map />} />
          <Route path="/reminders" element={<Reminders />} />
        </Routes>
        <nav className="fixed bottom-0 left-0 right-0 bg-gray-100 p-4">
          <ul className="flex justify-around">
            <li>
              <Link to="/" className="flex flex-col items-center text-gray-600">
                <List size={24} />
                <span>Wish List</span>
              </Link>
            </li>
            <li>
              <Link to="/map" className="flex flex-col items-center text-gray-600">
                <MapPin size={24} />
                <span>Map</span>
              </Link>
            </li>
            <li>
              <Link to="/reminders" className="flex flex-col items-center text-gray-600">
                <Calendar size={24} />
                <span>Reminders</span>
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    </Router>
  );
};

export default App;
