    import React, { useState, useEffect } from 'react';
    import { database } from '../services/firebase';
    import { ref, onValue } from 'firebase/database';

    const Reminders = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const eventsRef = ref(database, 'events');
        const unsubscribe = onValue(eventsRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
            const eventList = Object.values(data);
            setEvents(eventList);
        } else {
            setEvents([]);
        }
        });

        return () => unsubscribe();
    }, []);

    const sortedEvents = events.sort((a, b) => new Date(a.date) - new Date(b.date));
    const nextEvent = sortedEvents.find(event => new Date(event.date) >= new Date());
    const pastEvents = sortedEvents.filter(event => new Date(event.date) < new Date());

    return (
        <div className="page">
        <h2 className="page-title">Reminders</h2>
        {nextEvent && (
            <div className="next-event">
            <h3>Next Event</h3>
            <p>{nextEvent.title} - {nextEvent.date} at {nextEvent.location}</p>
            </div>
        )}
        <h3>Past Events</h3>
        <ul className="event-list">
            {pastEvents.map((event, index) => (
            <li key={index} className="event-item">
                {event.title} - {event.date} at {event.location}
            </li>
            ))}
        </ul>
        </div>
    );
    };

    export default Reminders;
