    import React, { useState, useEffect } from 'react';
    import { database } from '../services/firebase';
    import { ref, push, remove, onValue } from 'firebase/database';
    import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
    import DatePicker from 'react-datepicker';
    import 'react-datepicker/dist/react-datepicker.css';
    import 'leaflet/dist/leaflet.css';

    const categories = ['Birthday', 'Anniversary', 'Holiday', 'Other'];

    const WishList = () => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ 
        title: '', 
        date: new Date().toISOString().split('T')[0], // Formato YYYY-MM-DD
        location: '', 
        category: 'Other',
        coordinates: [51.505, -0.09]
        });
    const [showMap, setShowMap] = useState(false);

    useEffect(() => {
        const eventsRef = ref(database, 'events');
        const unsubscribe = onValue(eventsRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
            const eventList = Object.entries(data).map(([key, value]) => ({
            id: key,
            ...value,
            }));
            setEvents(eventList);
        } else {
            setEvents([]);
        }
        });

        return () => unsubscribe();
    }, []);

    const addEvent = (e) => {
        e.preventDefault();
        const eventsRef = ref(database, 'events');
        push(eventsRef, {
            ...newEvent,
          date: newEvent.date, // Ya está en formato ISO
            coordinates: newEvent.coordinates.map(coord => parseFloat(coord.toFixed(6)))
        });
        setNewEvent({ 
            title: '', 
            date: new Date().toISOString().split('T')[0],
            location: '', 
            category: 'Other',
            coordinates: [51.505, -0.09]
        });
        setShowMap(false);
    };

    const deleteEvent = (id) => {
        const eventRef = ref(database, `events/${id}`);
        remove(eventRef);
    };

    const LocationPicker = () => {
        const map = useMapEvents({
        click(e) {
            setNewEvent(prev => ({ ...prev, coordinates: [e.latlng.lat, e.latlng.lng] }));
        },
        });

        return (
        <Marker position={newEvent.coordinates}>
            <Popup>Selected location</Popup>
        </Marker>
        );
    };

    return (
        <div className="page">
        <h2 className="page-title">Wish List</h2>
        <form onSubmit={addEvent} className="event-form">
            <input
            type="text"
            placeholder="Event title"
            value={newEvent.title}
            onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
            className="input"
            required
            />
        <input
            type="date"
            value={newEvent.date}
            onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
                className="input"
            required
            />
            <input
            type="text"
            placeholder="Location"
            value={newEvent.location}
            onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
            className="input"
            onFocus={() => setShowMap(true)}
            required
            />
            <select
            value={newEvent.category}
            onChange={(e) => setNewEvent({ ...newEvent, category: e.target.value })}
            className="input"
            >
            {categories.map(category => (
                <option key={category} value={category}>{category}</option>
            ))}
            </select>
            <button type="submit" className="button">Add Event</button>
        </form>
        {showMap && (
            <div style={{ height: '300px', marginBottom: '20px' }}>
            <MapContainer center={newEvent.coordinates} zoom={13} style={{ height: '100%', width: '100%' }}>
                <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                <LocationPicker />
            </MapContainer>
            </div>
        )}
        <ul className="event-list">
            {events.map((event) => (
            <li key={event.id} className="event-item">
                <strong>{event.title}</strong> - {new Date(event.date).toLocaleDateString()} at {event.location}
                <br />
                Category: {event.category}
                <button onClick={() => deleteEvent(event.id)} className="delete-button">Delete</button>
            </li>
            ))}
        </ul>
        </div>
    );
    };

    export default WishList;
